    // flag to indicate chat status
    private bool isChatting = false;

    // name of the user who will be chatting
    private string username;

    // on-screen message
    private string message;

    // new message input
    private string newMessage;

    // list of messages in chat
    private List<Message> messages = new List<Message>();

    private string hubUrl;
    private HubConnection hubConnection;

    public async Task Chat()
    {
        // check username is valid
        if (string.IsNullOrWhiteSpace(username))
        {
            message = "Please enter a name";
            return;
        };

        try
        {
            // Start chatting and force refresh UI.
            isChatting = true;
            await Task.Delay(1);

            // remove old messages if any
            messages.Clear();

            // Create the chat client
            string baseUrl = navigationManager.BaseUri;

            hubUrl = baseUrl.TrimEnd('/') + ChatHub.HubUrl;

            hubConnection = new HubConnectionBuilder()
                .WithUrl(hubUrl)
                .Build();

            hubConnection.On<string, string>("Broadcast", BroadcastMessage);

            await hubConnection.StartAsync();

            await SendAsync($"[Notice] {username} joined chat room.");
        }
        catch (Exception e)
        {
            message = $"ERROR: Failed to start chat client: {e.Message}";
            isChatting = false;
        }
    }

    private void BroadcastMessage(string name, string message)
    {
        bool isMine = name.Equals(username, StringComparison.OrdinalIgnoreCase);

        messages.Add(new Message(name, message, isMine));

        // Inform blazor the UI needs updating
        StateHasChanged();
    }

    private async Task DisconnectAsync()
    {
        if (isChatting)
        {
            await SendAsync($"[Notice] {username} left chat room.");

            await hubConnection.StopAsync();
            await hubConnection.DisposeAsync();

            hubConnection = null;
            isChatting = false;
        }
    }

    private async Task SendAsync(string message)
    {
        if (isChatting && !string.IsNullOrWhiteSpace(message))
        {
            await hubConnection.SendAsync("Broadcast", username, message);

            newMessage = string.Empty;
        }
    }

    private class Message
    {
        public Message(string username, string body, bool mine)
        {
            Username = username;
            Body = body;
            Mine = mine;
        }

        public string Username { get; set; }
        public string Body { get; set; }
        public bool Mine { get; set; }

        public bool IsNotice => Body.StartsWith("[Notice]");

        public string CSS => Mine ? "sent" : "received";
    }

